function drawPie3dExploded() {
    var data = google.visualization.arrayToDataTable([
            ["Task", "Hours per Day"],
            ["Work", 11],
            ["Eat", 2],
            ["Commute", 2],
            ["Watch TV", 2],
            ["Sleep", 7],
        ]),
        options_pie3d_exploded = {
            title: "My Daily Activities",
            is3D: !0,
            pieSliceText: "label",
            height: 400,
            fontSize: 12,
            colors: ["#99B898", "#FECEA8", "#FF847C", "#E84A5F", "#474747"],
            chartArea: { left: "5%", width: "90%", height: 350 },
            slices: {
                1: { offset: 0.2 },
                2: { offset: 0.15 },
                3: { offset: 0.16 },
                4: { offset: 0.12 },
            },
        },
        pie3dExploded = new google.visualization.PieChart(
            document.getElementById("pie-3d-exploded")
        );
    pie3dExploded.draw(data, options_pie3d_exploded);
}
google.load("visualization", "1.0", { packages: ["corechart"] }),
    google.setOnLoadCallback(drawPie3dExploded),
    $(function () {
        function resize() {
            drawPie3dExploded();
        }
        $(window).on("resize", resize), $(".menu-toggle").on("click", resize);
    });
